const { EdgeFunction } = require('@vercel/build-utils');

exports.version = 3;

exports.build = async ({ entrypoint, files }) => {
    const output = new EdgeFunction({
        name: entrypoint,
        deploymentTarget: 'v8-worker',
        entrypoint,
        files: {
            [entrypoint]: files[entrypoint]
        },
    });
    return { output };
};
