const { Lambda, EdgeFunction } = require('@vercel/build-utils');

exports.build = async () => {
    const lambda = new Lambda({
        files: {},
        runtime: 'provided',
        handler: 'example.js'
    });
    const edge = new EdgeFunction({
      files: {},
      deploymentTarget: 'v8-worker',
      entrypoint: 'example.js'
    });
    const output = {
      lambda,
      lambda2: lambda,
      edge,
      edge2: edge
    };
    return { output };
};
