const { FileBlob, Lambda } = require('@vercel/build-utils');

exports.build = async () => {
    const file = new FileBlob({
        data: Buffer.from('file contents')
    });
    const lambda = new Lambda({
        files: {},
        runtime: 'provided',
        handler: 'example.js'
    })
    const output = {
        file,
        'withTrailingSlash/': lambda
    };
    return { output };
};
