/**
 * Zod schemas for uv configuration types.
 *
 * Types are defined in types.ts (source of truth).
 * Schemas are generated by ts-to-zod and re-exported here with proper typing.
 *
 * @module uv-config/schema
 */

import type { z } from 'zod';

import {
  uvConfigSchema as generatedUvConfigSchema,
  uvConfigWorkspaceSchema as generatedUvConfigWorkspaceSchema,
  uvIndexEntrySchema as generatedUvIndexEntrySchema,
} from './schema.zod';
import type { UvConfig, UvConfigWorkspace, UvIndexEntry } from './types';

/**
 * Schema for uv workspace configuration.
 */
export const UvConfigWorkspaceSchema =
  generatedUvConfigWorkspaceSchema.passthrough() as z.ZodType<UvConfigWorkspace>;

/**
 * Schema for uv index entry configuration.
 */
export const UvIndexEntrySchema =
  generatedUvIndexEntrySchema.passthrough() as z.ZodType<UvIndexEntry>;

/**
 * Schema for [tool.uv] section in pyproject.toml or uv.toml.
 */
export const UvConfigSchema =
  generatedUvConfigSchema.passthrough() as z.ZodType<UvConfig>;
